import React, { useState, Fragment } from "react";
import Form from "./Form";
import Table from "./components/Table";
function Profile() {
  
  const [tableData, setTableData] = useState([]);
  const [formObject, setFormObject] = useState({
    ID: "",
    Course: "",
    Description: ""
  });
  const REST_API = 'http://localhost:9090/addcourses/';
   
    function getBooks(){
        return fetch(REST_API,{ 
            method: 'get',
                headers: {
                'Accept': 'application/json, text/plain, */*',
                'Content-Type': 'application/json',
                },
                'credentials': 'cross-origin'
        })
        .then(res => res.json());  
             
    }
  
  const onValChange = (getBooks) => {
    

    const value = (res) => ({
      ...res,
      [getBooks.target.name]: getBooks.target.value,
    });
    setFormObject(value);
  };
  const onFormSubmit = (event) => {
    
    event.preventDefault();
    const checkVal = !Object.values(formObject).every((res) => res === "");
    if (checkVal) {
      const dataObj = (data) => [...data, formObject];
      setTableData(dataObj);
      const isEmpty = { Id: "", course: "", Description: "" };
      setFormObject(isEmpty);
    }
  };
  return (
    <Fragment>
      <Form
        onValChange={onValChange}
        formObject={formObject}
        onFormSubmit={onFormSubmit}
      />
      <Table tableData={tableData} />
    </Fragment>
  );
}
export default Profile;
