import React, {useState, useEffect} from 'react';
import "bootstrap/dist/css/bootstrap.min.css"
function Demo() {
    // 2. Create our *data* variable as well as the *setdata* function via useState
    // We're setting the default value of data to null, so that while we wait for the
    // fetch to complete, we dont attempt to render the data
  let [data, setdata] = useState(null)
  let headers = {}
    // 3. Create out useEffect function
    fetch("http://localhost:9090/getcourses" )
    .then((response) => {
      return response.json();
    })
    .then((student) => {
      setStudent(student);
      console.log(student);

      event.preventDefault();
    });
  return (
    <div >
    
      <table className="table">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Course</th>
            <th scope="col">Description</th>
            
          </tr>
        </thead>
        <tbody>
         
          {data && (data.map(item =>
            <tr>
              <th scope="row">{ item.id }</th>
              <td>{ item.course }</td>
              <td>{ item.Description }</td>
             
            </tr>
            ))}
        </tbody>
      </table>
    </div>
  );
}
export default Demo;
