import React, {useState, useEffect} from 'react'
import { Form, Button } from 'react-bootstrap';


function FormComponent(){
    const [id, setId] = useState("");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [data, setdata]=useState({
      id: id,ame: name,
      email: email,
  });
    
    function handleSubmit(e){
        e.preventDefault();
    fetch("http://localhost:8080/addUser ", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({id: id,
          name: name,
          email: email}),
      }).then((data=>
        setdata(data)))
        console.log("Hello at employee insert");
        console.log(id+ " "+name+ " "+ email)
     
  }
  function DeleteHandle(e){
    e.preventDefault();
  useEffect(() => {
    // DELETE request using fetch with set headers
    const requestOptions = {
        method: 'DELETE',
        headers: { 
            'Authorization': 'Bearer my-token',
            'My-Custom-Header': 'foobar'
        }
    };
    fetch('http://localhost:9090/courses/123', requestOptions)
        .then(() => setStatus('Delete successful'));
}, []);
}
    
  return (
    
    <div  className="formDisplay">
    <Row>
    <Form > 
      <Form.Group> 
          <Form.Label>Enter your name:</Form.Label> 
          <Form.Control type="text"  value={name}
                        placeholder="Enter your full name"onChange={(e) => setName(e.target.value)} /> 
        </Form.Group> 
        <Form.Group> 
          <Form.Label>Enter your email address:</Form.Label> 
          <Form.Control type="email" value={email}
                        placeholder="Enter your your email address"  onChange={(e) => setEmail(e.target.value)} /> 
        </Form.Group> 
        <Form.Group> 
          <Form.Label>Enter your age:</Form.Label> 
          <Form.Control type="number" placeholder="Enter your age" /> 
        </Form.Group> 
        <Button variant="primary" type="submit" onClick={handleSubmit}> 
           Click here to submit form 
        </Button> 
      </Form> 
      </Row>
      

    </div>
  );
}
export default FormComponent;
