import { Col, Card, CardBody, Button } from "react";
class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      dealdata: [],
    };
  }
  
  componentDidMount(){
          fetch('http://localhost:9090/courses/{courseId}')
          .then(res => {
            this.setState({ dealdata:res.data })
              console.log("recived deals : "+ this.state.dealdata);
              this.forceUpdate()
          })
  
        }
  
  deleteItem(courseId, e) {
    console.log("ids", courseId, e.target);
  }
  render() {
    return (
      <div>
        <Col xs="12" md="6">
          {this.state.dealdata.map(item => (
            <Card color="light" className="cards">
              <CardBody>
                <label>
                  {item.percentage}% Deal on {item.on}
                  {item._id}
                </label>
                <Button
                  size="sm"
                  className="btn-facebook btn-brand icon mr-1 mb-1"
                >
                  <span>EDIT</span>
                </Button>&nbsp;&nbsp;
                <Button
                  size="sm"
                  className="btn-youtube btn-brand icon mr-1 mb-1"
                  onClick={this.deleteItem.bind(this, item._courseId)}
                >
                  DELETE
                </Button>
              </CardBody>
            </Card>
          ))}
        </Col>
      </div>
    );
  }
  }
  export default Form
